import React from 'react';
import axios from 'axios';

import './App.css';

class App extends React.Component {


  state = {
    title: '',
    body: '',
    posts: []
  };

  componentDidMount = () => {
    this.getBlogPost();
  }

  getBlogPost = () => {
    axios.get('/api')
      .then((response) => {
        const data = response.data;
        this.setState({ posts: data });
        console.log('Data has been recieved.');
      })
      .catch(() => {
        alert('Error retrieving data.');
      });
  }

  handleChange = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value});
  };


  submit = (event) => {
    event.preventDefault();

    var x = document.getElementById("titleInput");
    var y = document.getElementById("bodyInput");

    if (x == "" || y == "") {
      alert("Post title and body must be filled before submission.");
      return false;
    } 

    const payload = {
      title: this.state.title,
      body: this.state.body
    };

    axios({
      url: '/api/save',
      method: 'POST',
      data: payload
    })
      .then(() => {
        console.log('Data has been sent to the server');
        this.resetUserInput();
        this.getBlogPost();
      })
      .catch(() => {
        console.log('Internal server error');
      });
  };

  resetUserInput = () => {
    this.setState({
      title: '',
      body: ''
    });
  };

  displayBlogPost = (posts) => {
    if (!posts.length) {
      return null;
    };

    var reverse = posts.reverse()

    return reverse.map((post, index) => {
      return <div className="blogPost">
              <div key={index}>
                <h3>{post.title}</h3>
                <p>{post.body}</p>
              </div>
            </div>
    });
  };




  render () {


    console.log('State: ', this.state);

    // JSX 

    return(
      <div className="app">
        <div className="sidebar">
          <h1>Anser's Online Notepad</h1>
          <form onSubmit={this.submit}>
            <div>
              <input 
                id="titleInput"
                className="titleInput"
                autoComplete="off"
                type="text"
                name="title"
                value={this.state.title}
                onChange={this.handleChange}
                placeholder="Title"
              />
            </div>
            <div>
              <textarea
                id="bodyInput"
                className="bodyInput"
                name="body"
                cols="30"
                rows="10"
                value={this.state.body}
                onChange={this.handleChange}
                placeholder="Body"
              >
              </textarea>
            </div>
            <button className="submit">Submit</button>
          </form>
        </div>
        <div className="postArea">
          {this.displayBlogPost(this.state.posts)}
        </div>  
      </div>
    );
  }
}

export default App;
