import React from 'react';
import axios from 'axios';

import './App.css';

import { FaTrash } from 'react-icons/fa';
import { FaCopy } from 'react-icons/fa';
import { FaEdit } from 'react-icons/fa';


class App extends React.Component {


  state = {
    title: '',
    body: '',
    posts: []
  };

  componentDidMount = () => {
    this.getBlogPost();
  }

  getBlogPost = () => {
    axios.get('/api')
      .then((response) => {
        const data = response.data;
        this.setState({ posts: data });
        console.log('Data has been recieved.');
      })
      .catch(() => {
        alert('Error retrieving data.');
      });
  }

  handleChange = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value});
  };


  submit = (event) => {

    var x = document.getElementById("titleInput").value;
    var y = document.getElementById("bodyInput").value;

    if (x === "" || y === "") {
      alert("Fill the title and body before submitting your post.");
      return false;
    } 

    event.preventDefault();

    const payload = {
      title: this.state.title,
      body: this.state.body
    };

    axios({
      url: '/api/save',
      method: 'POST',
      data: payload
    })
      .then(() => {
        console.log('Data has been sent to the server');
        this.resetUserInput();
        this.getBlogPost();
      })
      .catch(() => {
        console.log('Internal server error');
      });
  };

  resetUserInput = () => {
    this.setState({
      title: '',
      body: ''
    });
  };

  displayBlogPost = (posts) => {
    if (!posts.length) {
      return null;
    };

    var reverse = posts.reverse()

    return reverse.map((post, index) => {
      return <div className="blogPost">
              <div key={index}>
                <h3 className="postTitle">{post.title}</h3>
                <p>{post.body}</p>
                <a className="trashIcon" href="#">
                  <FaTrash size={25} />
                </a>
                <a className="copyIcon" href="#" onClick={this.copy}>
                  <FaCopy size={25} />
                </a>
                <a className="editIcon" href="#">
                  <FaEdit size={25} />
                </a>
              </div>
            </div>
    });
  };

  copy = (event) => {
    event.preventDefault();
    event.persist();
    var noteContent = event.currentTarget.parentNode.children[0].innerHTML + "\n" + event.currentTarget.parentNode.children[1].innerHTML;
    event.clipboardData.setData("text/plain", noteContent);



  }


  render () {


    console.log('State: ', this.state);

    // JSX 

    return(
      <div className="app">
        <div className="sidebar">
          <h1>Anser's Online Notepad</h1>
          <form onSubmit={this.submit}>
            <div>
              <input 
                id="titleInput"
                className="titleInput"
                autoComplete="off"
                type="text"
                name="title"
                value={this.state.title}
                onChange={this.handleChange}
                placeholder="Title"
              />
            </div>
            <div>
              <textarea
                id="bodyInput"
                className="bodyInput"
                name="body"
                cols="30"
                rows="10"
                value={this.state.body}
                onChange={this.handleChange}
                placeholder="Body"
              >
              </textarea>
            </div>
            <button className="submit">Submit</button>
          </form>
        </div>
        <div className="postArea">
          {this.displayBlogPost(this.state.posts)}
        </div>  
      </div>
    );
  }
}

export default App;
